INSERT INTO `block1` (`id`, `title`, `description`, `image`) VALUES
(1, 'CÔNG NGHỆ THÔNG MINH CHO TƯƠNG LAI', 'Đặt khách hàng làm trọng tâm, các mẫu xe thông minh của VinFast được ứng dụng những công nghệ ưu việt hàng đầu thế giới như trí tuệ nhân tạo, máy học và học sâu cùng các tính năng tự hành cấp độ cao, mở ra không gian hưởng thụ tiện nghi, giải trí hoàn hảo cùng trải nghiệm cá nhân hóa đáng giá nhất.', 'http://localhost:3000/images/block1/top_slide_1.png'),
(2, 'THIẾT KẾ ĐẦY ĐAM MÊ', 'Kết hợp với nhà thiết kế xe nổi tiếng thế giới Pininfarina, VinFast mang đến chất lượng thiết kế đẳng cấp cho từng dòng xe. Theo đuổi triết lý trải nghiệm chạm sinh học, những chiếc xe VinFast sở hữu vẻ ngoài sang trọng đặc trưng cùng khoang nội thất đậm chất tương lai, được chăm chút trong từng chi tiết.', 'http://localhost:3000/images/block1/top_slide_2.webp'),
(3, 'ĐẲNG CẤP AN TOÀN QUỐC TẾ', 'Đặt sự an tâm của khách hàng lên trên hết, những chiếc xe của VinFast được trang bị các tính năng an toàn tối tân nhất để bảo vệ người lái và mọi hành khách trên xe, đáp ứng tiêu chuẩn khắt khe của các tổ chức đánh giá xe uy tín hàng đầu thế giới như ASEAN NCAP, EURO NCAP, NHTSA...', 'http://localhost:3000/images/block1/top_slide_3.webp'),
(4, 'TRẢI NGHIỆM XUẤT SẮC', 'Sở hữu xe VinFast chính là tận hưởng những giá trị tốt nhất của một hệ sinh thái đẳng cấp, từ mô hình O2O tích hợp thương mại điện tử và trải nghiệm tại hệ thống Đại lý/Showroom/trạm sạc rộng khắp, tới chất lượng dịch vụ hậu mãi vượt trội và sự tân tâm trong từng khoảnh khắc của khách hàng.', 'http://localhost:3000/images/block1/top_slide_4.webp'),
(5, 'CHÍNH SÁCH THẤU HIỂU', 'Mong muốn mọi người được sở hữu những chiếc xe tốt nhất, VinFast luôn sáng tạo với chính sách sách bán hàng và hậu mãi có lợi nhất cho khách hàng. VinFast cũng tiên phong thúc đẩy xu hướng sử dụng xe điện, hướng tới một tương lai xanh, bền vững hơn bằng chính sách pin độc đáo và chế độ bảo hành 10 năm duy nhất trên thị trường cho ô tô điện', 'http://localhost:3000/images/block1/top_slide_5.webp');
INSERT INTO `block2` (`id`, `quote`, `author`) VALUES
(1, 'VinFast, thương hiệu ô tô Việt thuộc Tập đoàn Vingroup là điển hình tiêu biểu của việc nhanh chóng phục hồi và có tiến bước nhanh chóng sau khi Việt Nam thành công chống dịch Covid...', '- Bloomberg -'),
(2, '“Trong ngành công nghiệp xe hơi, việc thiết kế và chế tạo không chỉ một mà hai chiếc xe chỉ trong vòng 12 tháng là tốc độ không tưởng”', '- Top Gear -'),
(3, 'Chìa khóa để đạt thành công ngay từ khâu sản xuất của VinFast chính là các đối tác mạnh mẽ như ABB, Bosch, Magna Steyr và Siemens', '- CNBC -');
