INSERT INTO `products` (`id`, `slug`, `title`, `image1`, `image3`, `image5`, `image6`, `categorySlug`, `dongcoBMW`, `description`, `hopSo`, `camGiacLai`, `congSuat`, `thietKeNgoaiThat`, `thietKeNgoaiThat2`, `thietKeNgoaiThat3`, `voLangCamXuc`, `daTienIch`, `giaiTri`, `thietKeNoiThat`, `thietKeNoiThat2`, `thietKeNoiThat3`, `dongCoCN`, `dongCoCN1`, `daiRongCao`, `chieuDai`, `moMen`, `khoangSang`, `dungTich`, `mucTieuThu`, `danDong`) VALUES
(1, 'lux-sa', 'VinFast LUX A2.0', 'http://localhost:3000/images/lux-sa/1.png', 'http://localhost:3000/images/lux-sa/3.png', 'http://localhost:3000/images/lux-sa/6.png', 'http://localhost:3000/images/lux-sa/6.png', 'Xe ô tô', '228 HP', '', 'ZF 8 CẤP', 'CHÂN THỰC', '228HP', 'MẠNH MẼ &', 'NĂNG ĐỘNG', 'Đột phá trong từng đường nét thiết kế, đón đầu xu hướng thời thượng nhưng vẫn mang đậm bản sắc Việt, LUX SA2.0 hứa hẹn trở thành một hiện tượng mới trên đường phố, chinh phục mọi ánh nhìn.', 'Thiết kế vô lăng hài hòa được bọc da sang trọng cho cảm giác lái chân thực trên mọi cung đường.', 'Sạc không dây và điều hòa tự động 2 vùng độc lập, kiểm soát chất lượng không khí bằng ion.', 'Màn hình trung tâm 10.4 inch và hệ thống âm thanh 13 loa khiến những chuyến đi trở nên vui hơn.\r\nHỗ trợ kết nối Apple Carplay.', 'LINH HOẠT &', 'HIỆN ĐẠI', 'Khoang hành khách của LUX SA2.0 nổi bật với không gian rộng rãi, chất liệu nội thất cao cấp cùng nhiều tiện ích thông minh phục vụ người lái và hành khách.', 'MÃNH LIỆT CHINH PHỤC TỪNG CUNG ĐƯỜNG', 'Sức mạnh của động cơ kết hợp cùng hàng loạt công nghệ an toàn tiên tiến, LUX SA 2.0 mang đến khả năng vận hành ưu việt, hoàn toàn tự tin cùng chủ nhân rẽ lối tiên phong, dấn thân vào những cuộc chơi lớn, luôn nổi bật từ đường phố đô thị đến địa hình mang tính thách thức.', '4940 x 1960 x 1773 (mm)', '2933 (mm)', '350 Nm', '150 (mm)', '85 (L)', 'Kết hợp: 8,39 (lít/100km)\r\nTrong đô thị: 10,46 (lít/100km)\r\nNgoài đô thị: 7,18 (lít/100km)', 'Cầu sau (RWD)'),
(2, 'lux-a', 'VinFast LUX A2.0', 'http://localhost:3000/images/lux-a/1.png', 'http://localhost:3000/images/lux-a/3.png', 'http://localhost:3000/images/lux-a/6.png', 'http://localhost:3000/images/lux-a/6.png', 'Xe ô tô', '2.0 L -228 HP', '', 'ZF 8 Cấp', 'Chân thực', '228HP', 'KHỎE KHOẮN &', 'SANG TRỌNG', 'Sự kết hợp giữa dáng vẻ khỏe khoắn và cấu trúc hoàn hảo của ngoại thất tạo nên điểm nhấn sang trọng nhưng vẫn đầy tinh tế cho LUX A2.0, thổi làn gió mới vào thiết kế đặc hữu của dòng sedan thông thường.', 'Được cấu thành từ nhôm, gỗ và da Nappa thượng hạng, khoang xe tạo cảm giác cao cấp và trang nhã.', 'Sạc không dây, điều hòa tự động 2 vùng độc lập kiểm soát chất lượng không khí bằng ion, rèm che nắng chỉnh điện, màn hình giải trí 10.4 inch cùng dàn âm thanh 13 loa. Hỗ trợ kết nối Apple Carplay.', 'Màn hình trung tâm 10.4 inch và hệ thống âm thanh 13 loa khiến những chuyến đi trở nên vui hơn. Hỗ trợ kết nối Apple Carplay.', 'TIỆN NGHI &', 'ĐẲNG CẤP', 'Chất liệu sang trọng, trau chuốt trong từng chi tiết, trang bị tiện nghi tiên tiến, tất cả làm nên một không gian đẳng cấp dành cho người lái và hành khách trên LUX A2.0.', 'MẠNH MẼ & ÊM ÁI', 'Sức mạnh 228 mã lực từ khối động cơ 2.0L tăng áp, kết hợp cùng hộp số tự động 8 cấp ZF và hàng loạt công nghệ an toàn tiên tiến, LUX A 2.0 mang đến khả năng vận hành chắc chắn, tự tin cùng chủ nhân chinh phục những cột mốc thành công.', '4973 x 1900 x 1500 (mm)', '2968 (mm)', '300 Nm', '150 (mm)', '70 (L)', 'Kết hợp: 8,39 (lít/100km)\r\nTrong đô thị: 11,11 (lít/100km)\r\nNgoài đô thị: 6,90 (lít/100km)', 'Cầu sau (RWD)'),
(3, 'fadil', 'VinFast FADIL', 'http://localhost:3000/images/fadil/1.jpg', 'http://localhost:3000/images/lux-a/3.png', 'http://localhost:3000/images/fadil/gray.png', 'http://localhost:3000/images/fadil/blue.png', 'Xe ô tô', '1.4 L -98 HP', '', 'NĂNG ĐỘNG', 'VƯỢT TRỘI', '228HP', 'HIỆN ĐẠI &', 'NĂNG ĐỘNG', 'Các đường nét thiết kế mang hơi thở châu Âu hòa quyện cùng những điểm nhấn đặc trưng của tinh thần Việt tạo nên một mẫu hatchback lý tưởng cho đô thị, nhỏ gọn và thông minh, sẵn sàng chinh phục mọi cung đường thành phố.', 'Không gian nội thất thoáng đãng và khoang hành lý rộng rãi tạo sự thoải mái cho mọi chuyến đi.', 'Màn hình giải trí 7 inch kết nối điện thoại và dàn âm thanh 6 loa đem lại trải nghiệm giải trí ấn tượng.', 'Nội thất bọc da, kết nối Bluetooth, đàm thoại rảnh tay, điều hòa tự động có cảm biến độ ẩm tạo nên một không gian thân thiện.', 'THOẢI MÁI &', 'TIỆN DỤNG', 'Chú trọng trải nghiệm của người dùng, Fadil mang đến khoang ngồi thoải mái bên trong một kích cỡ nhỏ gọn và đậm chất đô thị. Đồng thời tích hợp hệ thống tiện ích tiện dụng, hỗ trợ tối đa cho hành khách trên xe.', 'AN TOÀN &\r\nTHÔNG MINH', 'Tự tin lướt qua mọi con phố, an tâm trên những hành trình dài với động cơ 1.4L cùng hàng loạt tích hợp công nghệ thông minh và tiện dụng giúp tăng cường trải nghiệm lái. Hơn hết là hệ thống an toàn vượt trội nhất phân khúc, cho bạn vững tay lái trên mọi cung đường.', '3676 x 1632 x 1495 (mm)', '2385 (mm)', '128 Nm', '150 (mm)', '32 (L)', 'Kết hợp: 5,85 (lít/100km)\r\nTrong đô thị: 7,11 (lít/100km)\r\nNgoài đô thị: 5,11 (lít/100km)', 'Cầu trước'),
(4, 'president', 'VinFast PRESIDENT', 'http://localhost:3000/images/president/1.jpg', 'http://localhost:3000/images/lux-sa/3.png', 'http://localhost:3000/images/president/5.png', 'http://localhost:3000/images/president/6.jpg', 'Xe ô tô', 'V8 6.2L', '', 'ZF 8 CẤp', '500 CHIẾC', '420 HP', 'MẠNH MẼ &', 'SANG TRỌNG\r\n', 'President là chiếc SUV bề thế với thiết kế sang trọng cùng những đường gân dập nổi, lưới tản nhiệt hình kim cương ấn tượng và 18 lựa chọn màu sơn độc đáo để khẳng định khí chất riêng biệt của chủ nhân.', 'President sử dụng chất liệu da Nappa cao cấp từ Eagle Ottawa cùng gỗ ốp Veneer thượng hạng đến từ Italy.', 'Hàng ghế sau với các tính năng massage, sưởi và khoảng để chân rộng rãi cùng cửa sổ trời.', 'Màn hình trung tâm 12.3 inch sắc nét cùng hệ thống âm thanh 13 loa mang tới trải nghiệm giải trí đỉnh cao.', 'LỊCH LÃM &', 'TINH TẾ', 'Hàng ghế sau lùi thêm 20cm, mang đến không gian tiêu chuẩn thương gia và sự kết hợp hài hoà của các chất liệu cao cấp, nội thất của President tạo cảm giác sang trọng, lịch lãm và tinh tế trong từng đường nét.', 'UY LỰC & AN TOÀN', 'Là một trong những mẫu xe thương mại mạnh nhất thị trường, President sở hữu động cơ V8 6.2L đạt 420 mã lực mang lại trải nghiệm vận hành uy lực mà êm ái. Cùng với những công nghệ an toàn tân tiến, President sẽ đồng hành cùng chủ nhân bứt phá & chinh phục hành trình đi tới thành công.', '5146 x 1987 x 1760 (mm)', '3133 (mm)', '624 Nm', '183 (mm)', '85 (L)', 'Kết hợp: 15,83 (lít/100km)\r\nTrong đô thị: 23,43 (lít/100km)\r\nNgoài đô thị: 11,58 (lít/100km)', '4 bánh toàn thời gian');
